            $('.listing-row').addClass('list-view');
            $(this).removeClass('text-muted').addClass('text-primary');
        }
        const expires = new Date(Date.now() + 30 * 864e5).toGMTString();
        document.cookie = "listing-view="+viewType+"-view; expires="+expires+"; path=/";
